mapper.select:
		SELECT comNum, syncType, modDate 
		FROM TB_SYNC 
		WHERE comNum = #{comNum} 
        AND syncType = #{syncType}

SELECT comNum, syncType, modDate
FROM TB_SYNC 
WHERE comNum = #{comNum}

mapper.update:
UPDATE TB_SYNC 
SET modDate = UNIX_TIMESTAMP()  
WHERE comNum = #{comNum}
AND syncType = #{syncType}
