mapper.select:
SELECT *
  FROM TB_OAUTH_CLIENT
 WHERE CLIENT_ID = #{clientID}

    and COM_NUM=#{comNum}

        SELECT count(1) as cnt
          FROM TB_OAUTH_CLIENT
	      WHERE COM_NUM = #{comNum}

mapper.insert:
INSERT INTO TB_OAUTH_CLIENT
( COM_NUM
, CLIENT_ID
, CLIENT_SECRET
, GRANT_TYPES
, AUTHORITIES
, SCOPE
, RESOURCE_IDS
, ADDITIONAL_INFORMATION
, REDIRECT_URI
, ACCESS_TOKEN_VALIDITY
, REFRESH_TOKEN_VALIDITY
, MEMO
, MOD_DATE
, REG_DATE
)VALUES
( #{comNum}
, #{clientID}
, #{clientSecret}
, #{grantTypes}
, #{authorities}
, #{scope}
, #{resourceIDs}
, #{additionalInformation}
, #{redirectUri}
, #{accessTokenValidity}
, #{refreshTokenValidity}
, #{memo}
, 0
, FN_UNIX_TIMESTEMP(SYSDATE))
